        <option value="validate-alpha" <?php echo (isset($data) && $data == 'validate-alpha') ? 'selected="selected"':'' ?>><?php echo __('Alpha (contains only letters)','kontrolwp')?></option>
        <option value="validate-alphanum" <?php echo (isset($data) && $data == 'validate-alphanum') ? 'selected="selected"':'' ?>><?php echo __('Alphanumeric (contains only letters and numbers)','kontrolwp')?></option>
