        <option value="<?php echo (isset($data) && strpos($data, 'length:') !== FALSE) ? $data:'' ?>" <?php echo (isset($data) && strpos($data, 'length:') !== FALSE) ? 'selected="selected"':'' ?> class="custom-val" customValFormat="length:%s" customLabelFormat="<?php echo __('Length','kontrolwp')?> (%s <?php echo __('characters','kontrolwp')?>)" confirmText="<?php echo __('Enter the character length required for this field: eg. 40','kontrolwp')?>"><?php echo __('Length','kontrolwp')?> <?php echo (isset($data) && strpos($data, 'length:') !== FALSE) ? '('.substr($data, 7, strlen($data)).' '.__('characters','kontrolwp').')':'' ?></option>
        <option value="<?php echo (isset($data) && strpos($data, 'minLength:') !== FALSE) ? $data:'' ?>" <?php echo (isset($data) && strpos($data, 'minLength:') !== FALSE) ? 'selected="selected"':'' ?> class="custom-val" customValFormat="minLength:%s" customLabelFormat="<?php echo __('Min Length','kontrolwp')?> (%s <?php echo __('characters','kontrolwp')?>)" confirmText="<?php echo __('Enter the minimum character length for this field: eg. 40','kontrolwp')?>"><?php echo __('Min Length','kontrolwp')?> <?php echo (isset($data) && strpos($data, 'minLength:') !== FALSE) ? '('.substr($data, 10, strlen($data)).' '.__('characters','kontrolwp').')':'' ?></option>
        <option value="<?php echo (isset($data) && strpos($data, 'maxLength:') !== FALSE) ? $data:'' ?>" <?php echo (isset($data) && strpos($data, 'maxLength:') !== FALSE) ? 'selected="selected"':'' ?> class="custom-val" customValFormat="maxLength:%s" customLabelFormat="<?php echo __('Max Length','kontrolwp')?> (%s <?php echo __('characters','kontrolwp')?>)" confirmText="<?php echo __('Enter the maximum character length for this field: eg. 40','kontrolwp')?>"><?php echo __('Max Length','kontrolwp')?> <?php echo (isset($data) && strpos($data, 'maxLength:') !== FALSE) ? '('.substr($data, 10, strlen($data)).' '.__('characters','kontrolwp').')':'' ?></option>
